import React from 'react'
import {useCart} from 'react-use-cart';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';

const style = {
  height:"60px"
}
const Cart = () => {
    const {isEmpty,totalUniqueItems,items,updateItemQuantity} = useCart();
    if(isEmpty) return <p>This Cart Is Empty</p>
    return (
        <>
          <p>Your Total Item Is {totalUniqueItems}</p>  
          <table class="table">
                <tbody>
                 {
                   items.map((itemsData)=>{
                      return(
                        <tr>
                          <td><img src={itemsData.image} alt="random img" width="100" height="100"/></td>
                          <td>{itemsData.name}</td>
                          <td>
                          <Button variant="contained" color="primary"> <RemoveIcon/></Button>
                          <Button variant="contained" style={{marginLeft:"10px"}}><AddIcon  /></Button>&nbsp;&nbsp;
                          <Button variant="contained" color="error">Clear Cart</Button>
                          </td>
                        </tr>
                      )
                   })
                 }                  
                </tbody>
           </table>
        </>
    )
}

export default Cart;
