import React from 'react'
import {useCart} from 'react-use-cart';

const Cart = () => {
    const {isEmpty,totalUniqueItems,items} = useCart();
    if(isEmpty) return <p>This Cart Is Empty</p>
    return (
        <>
          <p>Your Total Item Is {totalUniqueItems}</p>  
          <table class="table">
                <tbody>
                 {
                   items.map((itemsData)=>{
                      return(
                        <tr>
                          <td><img src={itemsData.image} alt="random img" width="100" height="100"/></td>
                          <td>Otto</td>
                          <td>@mdo</td>
                        </tr>
                      )
                   })
                 }                  
                </tbody>
           </table>
        </>
    )
}

export default Cart;
